window.storyFormat({"name":"Paperthin","version":"1.2.0","description":"The default proofing format for Twine 2. Icon designed by <a href=\"http://www.thenounproject.com/Simon Child\">Simon Child</a> from the <a href=\"http://www.thenounproject.com\">Noun Project</a>","author":"<a href=\"http://chrisklimas.com\">Chris Klimas</a>","image":"icon.svg","url":"http://twinery.org/","license":"ZLib/Libpng","proofing":true,"source":"<!DOCTYPE html>\n<html>\n<head>\n<title>{{STORY_NAME}}</title>\n<meta charset=\"utf-8\">\n<style>\nbody\n{\n\tfont: 10pt Cousine, monospace;\n\tmargin: 2em;\n}\n\nh1\n{\n\tfont-size: 14pt;\n\ttext-align: center;\n\tmargin-bottom: 2em;\n}\n\ntw-storydata\n{\n\tdisplay: block !important;\n}\n\ntw-passagedata\n{\n\tdisplay: block !important;\n\tline-height: 200%;\n\tmargin-bottom: 2em;\n\twhite-space: pre-wrap;\n}\n\ntw-passagedata + tw-passagedata\n{\n\tborder-top: 1pt dashed black;\n\tpadding-top: 2em;\n}\n\n.passage-name\n{\n\tdisplay: block;\n\tfont-weight: bold;\n\tmargin-bottom: 0.5em;\n}\n\n</style>\n</head>\n<body>\n\t<h1>{{STORY_NAME}}</h1>\n{{STORY_DATA}}\n<script>\n// Get all tw-passagedata elements.\nvar elements = document.querySelectorAll('tw-passagedata');\n// Loop through each element.\nelements.forEach(function(element) {\n\t// Create a new span element for the passage name.\n\tvar passageName = document.createElement('span');\n\t// Set the class name for styling.\n\tpassageName.className = 'passage-name';\n\t// Set the text content to the passage's name.\n\tpassageName.textContent = element.getAttribute('name');\n\t// Insert the passage name before the passage content.\n\telement.insertBefore(passageName, element.firstChild);\n});\n</script>\n</body>\n</html>\n"});